import React from 'react';
import { useContext, useState } from 'react';
import { RoomContext } from '../context';
import Title from './Title';

// get all unique values
const getUnique = (items, value) => {
    return [...new Set(items.map(item => item[value]))]
}

export default function RoomFilter({rooms}) {
    const [charge, setCharge] = useState(7.5);
    const getTariff = () => {
        fetch('http://api.currencylayer.com/live?access_key=b2ac9cdf32ab43c4d02400992a0e0580')
        .then(response => response.json())
        .then(data => {
            const usdinr = Object(data.quotes)["USDINR"];
            let tariff = usdinr / 10;
            setCharge(tariff);
        })
        .catch(error => {
            console.log("Error : ", error);
            setCharge(7.5);
        });
    };
    getTariff();
    const context = useContext(RoomContext);
    const { handleChange, type, capacity, price, minPrice, maxPrice, minSize, maxSize, breakfast, pets } = context;
    // get unique types
    let types = getUnique(rooms, 'type');
    // add all
    types = ['all', ...types];
    types = types.sort();
    // map to jsx
    types = types.map((item, index) => {
        return(
            <option value={item} key={index} >
                {item}
            </option>
        );
    })

    let people = getUnique(rooms, 'capacity');
    people = people.map((item, index) => {
        return(
            <option value={item} key={index} >
                {item}
            </option>
        );
    })
    return (
        <section className="filter-container">
            <Title title="search rooms" />
            <form className="filter-form" >
                {/* select type */}
                <div className="form-group">
                    <label htmlFor="type">room type</label>
                    <select name="type" id="type" value={type} className="form-control" onChange={handleChange}>
                        {types}
                    </select>
                </div>
                {/* end of select type */}

                {/* select guests */}
                <div className="form-group">
                    <label htmlFor="capacity">number of guests</label>
                    <select name="capacity" id="capacity" value={capacity} className="form-control" onChange={handleChange}>
                        {people}
                    </select>
                </div>
                {/* end of select guests */}

                {/* room price */}
                <div className="form-group">
                    <label htmlFor="price">room price ₹{Math.round(price * parseFloat(charge)) - (Math.round(price * parseFloat(charge)) % 10)}</label>
                    <input type="range" name="price" min={minPrice} max={maxPrice} id="price" value={price} onChange={handleChange} />
                </div>
                {/* end of room price */}
            </form>
        </section>
    )
}
